#!/usr/bin/env python3

import rclpy
from rclpy.node import Node
from geometry_msgs.msg import TransformStamped
from tf2_ros import TransformBroadcaster


class TFBroadcaster(Node):
    def __init__(self):
        super().__init__('tf_broadcaster_node')

        # สร้าง TF Broadcaster
        self.br = TransformBroadcaster(self)

        # ส่ง TF ทุก 0.1 วินาที (10Hz)
        self.timer = self.create_timer(0.1, self.broadcast_tf)

    def broadcast_tf(self):
        t = TransformStamped()

        # Timestamp
        t.header.stamp = self.get_clock().now().to_msg()

        # ต้นทาง: base_link → ปลายทาง: laser_frame
        t.header.frame_id = 'base_link'
        t.child_frame_id = 'laser_frame'

        # กำหนดตำแหน่งของ laser_frame จาก base_link
        t.transform.translation.x = 0.1
        t.transform.translation.y = 0.0
        t.transform.translation.z = 0.1

        # ไม่มีการหมุน
        t.transform.rotation.x = 0.0
        t.transform.rotation.y = 0.0
        t.transform.rotation.z = 0.0
        t.transform.rotation.w = 1.0

        # ส่ง TF
        self.br.sendTransform(t)

        # debug log
        self.get_logger().info('Broadcasted TF base_link -> laser_frame')

def main():
    rclpy.init()
    node = TFBroadcaster()
    rclpy.spin(node)
    rclpy.shutdown()


if __name__ == '__main__':
    main()
